SELECT id_station, EXTRACT(YEAR FROM time) AS "year", date_trunc('month', time) AS "month", date_trunc('DAY', time) AS "day", date_trunc('HOUR', time) AS "hour", <avg_s> FROM <db> where  time > TIMESTAMP '<timestamp>' - INTERVAL '<range>' <rangesUnit> AND time < TIMESTAMP '<timestamp>' AND id_station in <stid> GROUP BY id_station, "year", "month", "day", "hour";
